 include 'Base.php';
class Destination extends Base{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Destination_model');
    } 

    /*
     * Listing of destinations
     */
    function index($ajax=0)
    {
        if($_SESSION['user']==null ){
            redirect('/');
        }
        $data['destination'] = $this->Destination_model->get_all_destinations();

        if($ajax==0 ){

            $data['_view'] = 'destination/index';
            $data['add_modal']=$this->load->view('destination/add_modal',array(),true);
            $this->load->view('layouts/main',$data);

        }else{

            echo   $this->load->view('destination/index',$data,true);
        }
    }
    /*
     * Adding a new destination
     */
    function add()
    {
        $login=null;
        // print_r($_POST);die;
        if(!isset($_SESSION['user'])){
            redirect('/');
        }
        if(isset($_POST) && count($_POST) > 0)
        // check if the destination exists
        { $login=$this->Destination_model->get_destination_name( $this->input->post('name'));
            if($login!=null){
                echo 'This destination already exists';die;
            }
            $params = array(
                'name' => $this->input->post('name'),
            );

            $destination_id = $this->Destination_model->add_destination($params);
            echo 'ok';die;
        }
        else
        {
            echo 'nok';die;
        }
    }

    /*
     * Editing a destination
     */
    function edit($id)
    {
        if($_SESSION['user']==null ){
            redirect('/');
        }
        // check if the destination exists before trying to edit it
        $data['destination'] = $this->Destination_model->get_destination($id);
        
        if(isset($data['destination']['id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'name' => $this->input->post('name'),
                );

                $this->Destination_model->update_destination($id,$params);            
                redirect('destination/index');
            }
            else
            {
                $data['_view'] = 'destination/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The destination you are trying to edit does not exist.');
    } 

    /*
     * Deleting destination
     */
    function remove($id)
    {
        if($_SESSION['user']==null ){
            redirect('/');
        }
        $destination = $this->Destination_model->get_destination($id);

        // check if the destination exists before trying to delete it
        if(isset($destination['id']))
        {
            $this->Destination_model->delete_destination($id);
            redirect('destination/index');
        }
        else
            show_error('The destination you are trying to delete does not exist.');
    }
    
}
